mapper.select:
SELECT 
FROM sys_menu
WHERE is_deleted = 0

    AND name LIKE CONCAT('%', #{name}, '%')


    AND type = #{type}


    AND status = #{status}


    AND parent_id = #{parentId}

ORDER BY parent_id, sort

mapper.delete:
-- 物理删除：关联表不需要保留历史记录
DELETE FROM sys_menu_permission
WHERE menu_id = #{menuId}
