mapper.select:
SELECT t.id, t.project_id, p.name as project_name, t.iteration_id, i.name as iteration_name, t.parent_id, t.title, t.description, t.type, t.priority, t.assignee_id, t.status, t.plan_start_date, t.plan_end_date, t.actual_start_date, t.actual_end_date, t.estimate_hours, t.create_user_id, t.update_user_id, t.create_time, t.update_time, t.is_deleted FROM gsms_task t LEFT JOIN gsms_project p ON t.project_id = p.id AND p.is_deleted = 0 LEFT JOIN gsms_iteration i ON t.iteration_id = i.id AND i.is_deleted = 0
AND t.is_deleted = 0
AND t.project_id = #{projectId}
AND t.assignee_id = #{assigneeId}
AND t.status = #{status, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}
ORDER BY t.create_time DESC

SELECT DISTINCT t.id, t.project_id, p.name as project_name, t.iteration_id, i.name as iteration_name, t.parent_id, t.title, t.description, t.type, t.priority,
       t.assignee_id, t.status, t.plan_start_date, t.plan_end_date, t.actual_start_date, t.actual_end_date,
       t.estimate_hours, t.create_user_id, t.update_user_id, t.create_time, t.update_time, t.is_deleted
FROM gsms_task t
INNER JOIN gsms_project_member pm ON t.project_id = pm.project_id AND pm.is_deleted = 0
LEFT JOIN gsms_project p ON t.project_id = p.id AND p.is_deleted = 0
LEFT JOIN gsms_iteration i ON t.iteration_id = i.id AND i.is_deleted = 0

    AND t.is_deleted = 0 AND pm.user_id = #{userId}

        AND t.project_id = #{projectId}


        AND t.assignee_id = #{assigneeId}


        AND t.status = #{status, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}


ORDER BY t.create_time DESC

mapper.update:
UPDATE gsms_task
project_id = #{projectId}, iteration_id = #{iterationId}, parent_id = #{parentId}, title = #{title}, description = #{description}, type = #{type, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}, priority = #{priority, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}, assignee_id = #{assigneeId}, status = #{status, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}, plan_start_date = #{planStartDate}, plan_end_date = #{planEndDate}, actual_start_date = #{actualStartDate}, actual_end_date = #{actualEndDate}, estimate_hours = #{estimateHours}, update_user_id = #{updateUserId}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}
