mapper.select:
SELECT 
FROM gsms_work_hour wh
LEFT JOIN gsms_project p ON wh.project_id = p.id AND p.is_deleted = 0
LEFT JOIN gsms_task t ON wh.task_id = t.id AND t.is_deleted = 0


        AND wh.user_id = #{userId}


        AND wh.project_id = #{projectId}


        AND wh.task_id = #{taskId}


        AND wh.work_date >= #{startDate}


        AND wh.work_date <= #{endDate}

    AND wh.is_deleted = 0

ORDER BY wh.work_date DESC, wh.create_time DESC

mapper.update:
UPDATE gsms_work_hour
user_id = #{userId}, project_id = #{projectId}, task_id = #{taskId}, work_date = #{workDate}, hours = #{hours}, content = #{content}, status = #{status, typeHandler=com.baomidou.mybatisplus.core.handlers.MybatisEnumTypeHandler}, update_user_id = #{updateUserId}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}
